import React, { useRef, useState, Component, useEffect } from "react";
import { SingleEliminationBracket, DoubleEliminationBracket, Match, MATCH_STATES, SVGViewer } from '@g-loot/react-tournament-brackets';
import { useSelector, useDispatch} from "react-redux";
import {updateMatches} from "../../features/singles/singlesSlice";

function SinglesBracket() {
    const [width, height] = [1920,1080];
    const finalWidth = Math.max(width - 50, 500);
    const finalHeight = Math.max(height - 100, 500);

    // const matchArray = useSelector((state) => state.matches.value);
    const matchArray = useSelector((state) => state.singles.matches);

    console.log("REDUX")
    console.log(matchArray)
