import {createSlice} from "@reduxjs/toolkit";

export const singlesSlice = createSlice({
    name: "singles",
    initialState: {
        championshipBracketMatches: [
            {
                id: 1,
                name: "Semifinals",
                nextMatchId: 3,
                tournamentRoundText: "2",
                state: "DONE",
