                        name: "Andrew"
                    }
                ]
            },
            {
                id: 3,
                name: "Finals",
                nextMatchId: null,
                tournamentRoundText: "1",
                state: "DONE",
                "participants": [
                    {
                        id: "1",
                        isWinner: true,
                        name: "Theodore",
                    },
                    {
                        id: "2",
                        isWinner: false,
                        name: "TBD"
                    }
                ]
            }
        ],
        consolationBracketMatches: [
    {
        id: 1,
        name: "Semifinals",
        nextMatchId: 3,
        tournamentRoundText: "2",
        state: "DONE",
        participants: [
            {
                id: "1",
                isWinner: true,
                name: "Theodore",
            },
            {
                id: "4",
                isWinner: false,
                name: "Elvin"
            }
        ]
    },
    {
        id: 2,
        name: "Semifinals",
        nextMatchId: 3,
        tournamentRoundText: "2",
        state: "DONE",
        "participants": [
            {
                id: "2",
                isWinner: true,
                name: "Daniel",
            },
            {
                id: "3",
                isWinner: false,
                name: "Andrew"
            }
        ]
    },
    {
        id: 3,
        name: "Finals",
        nextMatchId: null,
        tournamentRoundText: "1",
        state: "DONE",
        "participants": [
            {
                id: "1",
                isWinner: true,
                name: "Theodore",
            },
            {
                id: "2",
                isWinner: false,
                name: "TBD"
            }
        ]
    }
]
    },

    reducers: {
        updateChampionshipBracketMatches: (state, action) => {
            state.championshipBracketMatches = structuredClone(action.payload);
        },
        updateConsolationBracketMatches: (state, action) => {
            state.consolationBracketMatches = structuredClone(action.payload);
        },
    },
});

export const {updateChampionshipBracketMatches} = singlesSlice.actions;
export const {updateConsolationBracketMatches} = singlesSlice.actions;

export default singlesSlice.reducer;
