import React, {useRef, useState, Component, useEffect} from "react";
import {
    SingleEliminationBracket,
    DoubleEliminationBracket,
    Match,
    MATCH_STATES,
    SVGViewer
} from '@g-loot/react-tournament-brackets';
import defaultTheme from "../Themes/defaultTheme";
import {useSelector, useDispatch} from "react-redux";
import {updateChampionshipBracketMatches} from "../../features/singles/singlesSlice";
import {Paper, Table, TableCell, TableContainer, TableHead, TableRow} from "@mui/material";

function SinglesBracket({}) {
    const dispatch = useDispatch()
    const groupArray = structuredClone(useSelector((state) => state.singles.roundRobinGroups));

    const [dispGroupArray, setDispGroupArray] = useState(groupArray);

    // async function getRoundRobinData() {
    // }
    function test() {
        console.log(groupArray);
        groupArray.forEach((row) => {
            console.log(row)
        })
    }

    return (
        <div>
            <button type="button" onClick={test}>RR</button>
            {groupArray.map((row) => {
                return(
                    <div key={row.groupNum}>
                        <h1>GROUP {row[0].groupNum}</h1>
                        <TableContainer component={Paper}>
                            <Table sx={{minWidth: 650}} aria-label="simple table">
                                <TableHead>
                                    <TableRow>
                                        <TableCell>Player Seed</TableCell>
                                        <TableCell>Player Name</TableCell>
                                        <TableCell>Player Placement</TableCell>
                                    </TableRow>
                                </TableHead>
                            </Table>
                        </TableContainer>
                    </div>

                )
            })}
            {groupArray.forEach((row) => {
                return(
                    <>
                        <h1>GROUP {row.groupNum}</h1>
                        <TableContainer component={Paper}>
                            <Table sx={{minWidth: 650}} aria-label="simple table">
                                <TableHead>
                                    <TableRow>
                                        <TableCell>Player Seed</TableCell>
                                        <TableCell>Player Name</TableCell>
                                        <TableCell>Player Placement</TableCell>
                                    </TableRow>
                                </TableHead>
                            </Table>
                        </TableContainer>
                    </>

                )
            })
            }


        </div>
    );
}

export default SinglesBracket;
